form.script:
function SetNavState(navid, VorH, initstate) {
   var tnavid;
   for(var i = 1; i < 5; i++) {
       if (i == 1) {;
           tnavid = navid;
       } else {
           tnavid = navid + i;
       }
       //alert(tnavid);
       navObj = document.getElementById(tnavid);
       if (navObj) {
           //alert(navObj.className.substring(0, 4));
           if (initstate == 's') {
               if (navObj.className.substring(0, 4) == VorH + 'mio') {
                   navObj.className = navObj.className.replace(VorH + 'mio',VorH + 'mis');
               } else {
                   navObj.className = navObj.className.replace(VorH + 'mis',VorH + 'mio');
               }
           } else {
               if (navObj.className.substring(0, 4) == VorH + 'mio') {
                   navObj.className = navObj.className.replace(VorH + 'mio',VorH + 'mi');
               } else {
                   navObj.className = navObj.className.replace(VorH + 'mi',VorH + 'mio');
               }
           }
       }
   }
}
